class E{constructor(t="ANIME_IS_COOL"){this.PREFIX=t}get=t=>sessionStorage.getItem(this.PREFIX+t);set=(t,o)=>sessionStorage.setItem(this.PREFIX+t,o);getAll=()=>Object.entries(sessionStorage).filter(([t,o])=>t.startsWith(this.PREFIX)).map(([t,o])=>({key:t.substring(this.PREFIX.length),value:o}));clear=()=>Object.entries(sessionStorage).filter(([t,o])=>t.startsWith(this.PREFIX)).forEach(([t,o])=>sessionStorage.removeItem(t))}class r{constructor(t,o){this.name=t,this.colors=o}}class e{static PRESETS=[new r("october",{"bg-color":"#342E37","text-color":"#F6E8EA","text-color-hover":"#FA824C","button-text-color":"white","button-bg-color":"#FA824C","button-bg-color-hover":"#FA824C40"}),new r("horizon",{"bg-color":"#312F2F","text-color":"#F6E8EA","text-color-hover":"#EF626C","button-text-color":"white","button-bg-color":"#EF626C","button-bg-color-hover":"#E8545E40"}),new r("dark",{"bg-color":"#2B2D42","text-color":"#8D99AE","text-color-hover":"#D90429","button-text-color":"white","button-bg-color":"#EF233C","button-bg-color-hover":"#D9042940"}),new r("beige-purple",{"bg-color":"#EFF8E2","text-color":"#23022E","text-color-hover":"#573280","button-text-color":"#EFF8E2","button-bg-color":"#573280","button-bg-color-hover":"#23022E40"})];static THEME_KEY="theme";static Store=new E;static setCurrent(t){Object.entries(t.colors).forEach(([o,s])=>document.documentElement.style.setProperty("--"+o,s)),e.Store.set(e.THEME_KEY,t.name)}static refresh(){const o=e.PRESETS.find(s=>s.name==e.Store.get(e.THEME_KEY))||e.PRESETS[0];e.setCurrent(o)}}export{e as T};
